-- Primeiro, vamos mover todas as páginas do módulo duplicado para o módulo principal
UPDATE pages SET moduleId = (SELECT id FROM modules WHERE slug = 'divisao' AND disciplineId = 1 LIMIT 1) 
WHERE moduleId IN (SELECT id FROM modules WHERE name = 'Divisão' AND disciplineId = 1 AND slug != 'divisao');

-- Agora deletar o módulo duplicado
DELETE FROM modules WHERE name = 'Divisão' AND disciplineId = 1 AND slug != 'divisao';

