query:
-- 1. Mover todas as páginas do Módulo 5 (Divisão duplicado) para o Módulo 4
UPDATE pages SET moduleId = (SELECT id FROM modules WHERE `order` = 4 AND disciplineId = 1 LIMIT 1) 
WHERE moduleId = (SELECT id FROM modules WHERE `order` = 5 AND disciplineId = 1 LIMIT 1);

-- 2. Deletar Módulo 5 (Divisão duplicado)
DELETE FROM modules WHERE `order` = 5 AND disciplineId = 1;

-- 3. Atualizar ordem da Porcentagem de 6 para 5
UPDATE modules SET `order` = 5, description = 'Cálculos com percentuais, proporções e regra de três' WHERE slug = 'porcentagem' AND disciplineId = 1;

-- 4. Atualizar descrição do módulo de Divisão unificado
UPDATE modules SET name = 'Divisão', description = 'Aprenda a dividir números, repartição justa, quociente, resto e aplicações práticas' WHERE `order` = 4 AND disciplineId = 1;

